mapper.select:
SELECT * FROM (SELECT FROM posts JOIN users ON posts.author = users.id
LEFT JOIN postLikes ON posts.id = postLikes.post AND #{loginUser} = postLikes.userId
WHERE posts.isDeleted = 'N'
AND posts.postDate < #{since}
AND posts.author = #{author}
ORDER BY postDate DESC) WHERE ROWNUM <= #{pages}

SELECT * FROM (SELECT FROM postLikes JOIN posts ON postLikes.post = posts.id JOIN users ON posts.author = users.id WHERE postLikes.userId = #{likedBy} AND posts.isDeleted = 'N' AND (SELECT isDeleted FROM users WHERE id = #{likedBy}) = 'N'
AND postLikes.likedTime < #{since}
ORDER BY likedTime DESC) WHERE ROWNUM <= #{pages}

mapper.delete:
DELETE FROM postlikes
 WHERE userId = #{loginUser}
   AND post = #{post}

mapper.select:
SELECT COUNT(*)
  FROM postLikes
 WHERE post = #{it}
