mapper.select:
	SELECT q.*, (SELECT name FROM users u WHERE u.id=q.writer) name
      FROM qna q
     WHERE id = #{id}

mapper.update:
UPDATE qna
   SET title=#{title}, content=#{content}
 WHERE id=#{id}

	UPDATE qna
	   SET isDeleted = 'Y'
 	 WHERE id=#{id}

UPDATE qnaComments
   SET content=#{content}
 WHERE id=#{id}
