mapper.select:
SELECT id, name, motd, profileImage,
isFollowingYou,
isFollowedByYou FROM users WHERE id = #{userId}

mapper.update:
UPDATE users
   SET name = #{name}
 WHERE id = #{userId}

UPDATE users
   SET motd = #{motd}
 WHERE id = #{userId}

mapper.select:
SELECT password
  FROM users
 WHERE id = #{userId}

mapper.update:
UPDATE users
   SET password = #{newPassword}
 WHERE id = #{userId}

mapper.select:
SELECT f.userId id, u.name name, u.profileImage profileImage,
isFollowingYou,
isFollowedByYou FROM follows f JOIN users u ON f.userId = u.id WHERE f.following = #{user}

SELECT f.following id, u.name name, u.profileImage profileImage,
isFollowingYou,
isFollowedByYou FROM follows f JOIN users u ON f.following = u.id WHERE f.userId = #{user}

mapper.delete:
DELETE FROM follows
 WHERE userId = #{loginUser}
   AND following = #{followingId}

mapper.select:
SELECT COUNT(*)
  FROM follows
 WHERE userId = #{loginUser}
   AND following = #{followingId}

SELECT id
  FROM users
 WHERE id = #{it}
